	private void initConfigClientMap(Map<String,Integer> map, Properties p, String pNamePrefix) {
		if(p!=null && p.size()>0) {
			Enumeration<Object> en = p.keys();
			while (en.hasMoreElements()) {
				Object o = en.nextElement();
				initConfigClientMap(map, o, p, pNamePrefix);
			}
		}
	}
	private void initConfigClientMap(Map<String,Integer> map, Object o, Properties p, String pNamePrefix) {
		if(o instanceof String key) {
			String v = p.getProperty(key);
			try {
				if(v!=null){
					v = v.trim();
					int vInt = java.lang.Integer.parseInt(v);
					map.put(key, vInt);
				}else{
					this.logWarn("propriet√† di govway '"+pNamePrefix+"'.key impostata vuota");
				}
			}catch(java.lang.Exception e) {
				this.logError("propriet√† di govway '"+pNamePrefix+"'.key possiede un valore ["+v+"] non corretto, errore:"+e.getMessage(),e);
			}  
		}
	}
	private Integer getConfigClientMap(Map<String,Integer> map, String hostname, int port, Integer defaultValue) {
		if(map==null || map.isEmpty()) {
			return defaultValue;
		}
		String key = hostname+"."+port;
		String key2 = hostname+":"+port; // NOTA: usare lo \ con i due punti nel file di propriet√† 'org.openspcoop2.pdd.connettori.syncClient.<nomeProperty>.localhost\:8090=valoreintero'
		if(map.containsKey(key)) {
			return map.get(key);
		}
		else if(map.containsKey(key2)) {
			return map.get(key2);
		}
		else if(map.containsKey(hostname)) {
			return map.get(hostname);
		}
		return defaultValue;
	}
	
