function IEVersione(){var n=window.navigator.userAgent,r=n.indexOf("MSIE ");if(r>0)return parseInt(n.substring(r+5,n.indexOf(".",r)),10);if(n.indexOf("Trident/")>0){var e=n.indexOf("rv:");return parseInt(n.substring(e+3,n.indexOf(".",e)),10)}var i=n.indexOf("Edge/");return i>0?parseInt(n.substring(i+5,n.indexOf(".",i)),10):-1}function isIE(){return IEVersione()>-1}